import React, { useReducer } from 'react'
import ButtonList from './ButtonList'
import VideoContainer from './VideoContainer'



const Maincontainer = () => {

    function updateAge(state, action) {
        if (action.type === 'ageIncrement') {
            return {
                age: state.age + 1,
                random: state.random
            }
        }
        if (action.type === 'ageDecrement') {
            return {
                age: state.age - 1,
                random: state.random
            }
        }
        if (action.type === 'genarateRandom') {
            return {
                age: state.age,
                random: Math.random()
            }
        }
        throw Error('Unknown action.');
    }

    const [state, dispatch] = useReducer(updateAge, { age: 10, random: 10101 })



    return (
        <div>
            <button
                onClick={() => {
                    dispatch({ type: 'ageIncrement' })
                }}
                className='p-2 m-2 bg-gray-500'>
                Increment
            </button>

            <button
                onClick={() => {
                    dispatch({ type: 'ageDecrement' })
                }}
                className='p-2 m-2 bg-gray-500'>
                Decrement
            </button>

            <button
                onClick={() => {
                    dispatch({ type: 'genarateRandom' })
                }}
                className='p-2 m-2 bg-gray-500'>
                Random
            </button>



            <p>Current Age  : {state.age}</p>
            <p>Random Number  : {state.random}</p>
            <ButtonList />
            <VideoContainer />
        </div>
    )
}

export default Maincontainer
