import { configureStore } from "@reduxjs/toolkit";
import appSlice from "./appSlice";
import { cacheResult } from "./searchSlice";

const appStore = configureStore({
    reducer: {
        app: appSlice,
        search: cacheResult
    }
})

export default appStore;
