import React, { useEffect } from 'react'
import ChatMessage from './ChatMessage'
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../utils/redux/chatSlice';

const LiveChat = () => {
    const dispatch = useDispatch()
    const chatMessages = useSelector((store) => store.chat.mesaage)

    useEffect(() => {
        const interval = setInterval(() => {
            dispatch(addMessage({
                name: 'Rohit',
                message: 'This is live chat'
            }))
        }, 2000)

        return () => clearInterval(interval)
    }, [])

    return (<div className='ml-2 p-2 border border-black h-[600px] w-64 rounded-lg bg-slate-100 overflow-y-scroll flex flex-col-reverse'>
        {chatMessages.reverse().map((item, index) => <ChatMessage key={index} name={item.name + index} message={item.message} />)}
    </div>
    )
}

export default LiveChat
